	private String getDescriptionsOfPhraseValue(String value, String objectTableID, NodeList referList) {
		StringBuffer buf = new StringBuffer();
		ArrayList<String> keywordList = new ArrayList<String>();
		String wrkStr, wrkStr2;
		String tableAlias, tableID, fieldID;
		StringTokenizer workTokenizer, workTokenizer2;
		org.w3c.dom.Element fieldElement, referElement;
		//
		int pos1 = value.indexOf("&", 0);
		int pos2;
		while (pos1 > -1) {
			pos2 = value.indexOf("&", pos1+1);
			if (pos2 == -1) {
				keywordList.add(value.substring(pos1, value.length()).trim());
				break;
			} else {
				keywordList.add(value.substring(pos1, pos2).trim());
			}
			pos1 = pos2;
		}
		//
		for (int i = 0; i < keywordList.size(); i++) {
			wrkStr = "";
			//
			if (keywordList.get(i).contains("&DataSource(")) {
				pos1 = keywordList.get(i).indexOf(")", 0);
				wrkStr = keywordList.get(i).substring(12, pos1);
			}
			if (keywordList.get(i).contains("&DataSourceImage(")) {
				pos1 = keywordList.get(i).indexOf(")", 0);
				wrkStr = keywordList.get(i).substring(17, pos1);
			}
			if (keywordList.get(i).contains("&Barcode(")) {
				pos1 = keywordList.get(i).indexOf(")", 0);
				wrkStr = keywordList.get(i).substring(9, pos1);
			}
			//
			if (wrkStr.equals("")) {
				buf.append(keywordList.get(i));
			} else {
				workTokenizer = new StringTokenizer(wrkStr, ";");
				wrkStr = workTokenizer.nextToken().trim();
				pos1 = wrkStr.indexOf(".");
				if (pos1 > -1) {
					fieldElement = null;
					tableAlias = wrkStr.substring(0, pos1);
					tableID = getTableIDOfTableAlias(tableAlias, referList, null);
					fieldID = wrkStr.substring(pos1+1, wrkStr.length());
					//
					if (tableID.equals(objectTableID)) {
						fieldElement = getSpecificFieldElement(tableID, fieldID);
					} else {
						for (int j = 0; j < referList.getLength(); j++) {
							referElement = (org.w3c.dom.Element)referList.item(j);
							if (referElement.getAttribute("TableAlias").equals(tableAlias) ||
									(referElement.getAttribute("TableAlias").equals("") && referElement.getAttribute("ToTable").equals(tableAlias))) {
								workTokenizer2 = new StringTokenizer(referElement.getAttribute("Fields"), ";" );
								while (workTokenizer2.hasMoreTokens()) {
									wrkStr2 = workTokenizer2.nextToken();
									if (wrkStr2.equals(fieldID)) {
										fieldElement = getSpecificFieldElement(tableID, fieldID);
										break;
									}
								}
								break;
							}
						}
					}
					//
					if (fieldElement == null) {
						buf.append(keywordList.get(i).replace(wrkStr, tableAlias + ".?????"));
					} else {
						if (fieldElement.getAttribute("Name").equals("")) {
							buf.append(keywordList.get(i).replace(wrkStr, tableAlias + "." + fieldID));
						} else {
							buf.append(keywordList.get(i).replace(wrkStr, tableAlias + "." + fieldElement.getAttribute("Name")));
						}
					}
				} else {
					buf.append(keywordList.get(i).replace(wrkStr, "?????"));
				}
			}
		}
		//
		return buf.toString();
	}
	
