	/**
	 * Setup contents-pane for node selected
	 * @param node :selected node
	 * @param refreshRequired :specify whether refresh required even if the same node selected again
	 */
	void setupContentsPaneForTreeNodeSelected(MainTreeNode node, boolean refreshRequired) {
		setupContentsPaneForTreeNodeSelected(node, null, refreshRequired);
	}

	/**
	 * Setup contents-pane for node selected
	 * @param node :selected node
	 * @param targetElement :selected element
	 * @param refreshRequired :specify whether refresh required even if the same node selected again
	 */
	void setupContentsPaneForTreeNodeSelected(MainTreeNode node, org.w3c.dom.Element targetElement, boolean refreshRequired) {
		try {
			setCursor(new Cursor(Cursor.WAIT_CURSOR));
			previousMainTreeNode = currentMainTreeNode;
			currentMainTreeNode = node;
			//
			if (currentMainTreeNode != previousMainTreeNode || refreshRequired) {
				if (previousMainTreeNode != null) {
					//
					previousMainTreeNode.updateFields();
					//
					setupJumpButtons();
				}
				//
				currentMainTreeNode.activateContentsPane(targetElement, false);
			}
			//
			//Setup frame title//
			this.setTitle(DialogAbout.APPLICATION_NAME + " - [" + currentFileName + "] - " + systemName + " " + systemVersion);
			//
			//Reset cursor//
		} finally {
			setCursor(new Cursor(Cursor.DEFAULT_CURSOR));
		}
	}

	/**
	 * Setup Jump-buttons
	 */
	void setupJumpButtons() {
		NodeJumpButton[] workNodeJumpButton = new NodeJumpButton[9];
		if (previousMainTreeNode != null) {
			jPanelJumpButtons.removeAll();
			//
			//Clear element of array(nodeJumpButton[i]) if it's//
			//not on jTree, or it's the same as previousNode.  //
			for (int i = 0; i < 8; i++) {
				if (nodeJumpButton[i] != null) {
					if (!nodeJumpButton[i].getNode().equals(systemNode)) {
						if (nodeJumpButton[i].getNode().getParent() == null) {
							nodeJumpButton[i] = null;
						}
					}
				}
				if (nodeJumpButton[i] != null) {
					if (nodeJumpButton[i].getNode().equals(previousMainTreeNode)) {
						nodeJumpButton[i] = null;
					}
				}
			}
			//
			//Save element(which is not null) into working-array in order,    //
			//while setting previousNode to the first element of working-array//
			int j = 0;
			if (currentMainTreeNode != previousMainTreeNode) {
				if (previousMainTreeNode.equals(systemNode)) {
					workNodeJumpButton[0] = new NodeJumpButton(previousMainTreeNode);
					j = 1;
				} else {
					if (previousMainTreeNode.getParent() != null) {
						workNodeJumpButton[0] = new NodeJumpButton(previousMainTreeNode);
						j = 1;
					}
				}
			}
			for (int i = 0; i < 8; i++) {
				if (nodeJumpButton[i] != null) {
					workNodeJumpButton[j] = nodeJumpButton[i];
					nodeJumpButton[i] = null;
					j = j + 1;
				}
			}
			//
			//Restore elements in array and add them to jPanelJumpButtons//
			for (int i = 0; i < 8; i++) {
				nodeJumpButton[i] = workNodeJumpButton[i];
				if (nodeJumpButton[i] != null) {jPanelJumpButtons.add(nodeJumpButton[i]);}
			}
			//
			//Refresh jPanelJumpButtons//
			jPanelJumpButtons.updateUI();
		}
	}
