	/***************************************
	 * Save edited content of current file *
	 ***************************************/
	void saveFileWithCurrentFileName() {
		if (currentFileName.startsWith("http:")
				|| currentFileName.startsWith("https:")
				|| currentFileName.startsWith("file:")) {
			JOptionPane.showMessageDialog(this, res.getString("ErrorMessage28"));
		} else {
			try{
				arrangeOrderOfDomElement();
				OutputFormat outputFormat = new OutputFormat(domDocument);
				outputFormat.setEncoding("UTF-8");
				FileOutputStream fileOutputStream = new FileOutputStream(currentFileName);
				OutputStreamWriter outputStreamWriter = new OutputStreamWriter(fileOutputStream, "UTF-8");
				Writer writer = new BufferedWriter(outputStreamWriter);
				XMLSerializer xmlSerializer = new XMLSerializer(writer, outputFormat);
				xmlSerializer.serialize(domDocument.getDocumentElement());
				writer.close();
				//
			} catch(Exception ex){
				JOptionPane.showMessageDialog(this, res.getString("ErrorMessage28"));
				ex.printStackTrace();
			}
		}
	}
	
	/**
	 * arrange order of DOM elements according to the XEAF-format specifications
	 */
	void arrangeOrderOfDomElement() {
		org.w3c.dom.Element topElement;
		NodeList nodeList;
		//
		nodeList = domDocument.getElementsByTagName("Function");
		if (nodeList.getLength() > 0) {
			topElement = (org.w3c.dom.Element)nodeList.item(0);
			topElement = sortElementsByType("Table", topElement);
			topElement = sortElementsByType("Subsystem", topElement);
			topElement = sortElementsByType("Menu", topElement);
		} else {
			nodeList = domDocument.getElementsByTagName("Table");
			if (nodeList.getLength() > 0) {
				topElement = (org.w3c.dom.Element)nodeList.item(0);
				topElement = sortElementsByType("Subsystem", topElement);
				topElement = sortElementsByType("Menu", topElement);
			} else {
				nodeList = domDocument.getElementsByTagName("Subsystem");
				if (nodeList.getLength() > 0) {
					topElement = (org.w3c.dom.Element)nodeList.item(0);
					topElement = sortElementsByType("Menu", topElement);
				}
			}
		}
	}

	/**
	 * sort elements with specific type
	 * @param type :type of elements to be sorted
	 * @param topElement :element where sorted element is inserted before
	 * @return Element :top element of sorted
	 */
	org.w3c.dom.Element sortElementsByType(String type, org.w3c.dom.Element topElement) {
		org.w3c.dom.Element element;
		NodeList nodeList = domDocument.getElementsByTagName(type);
		for (int i = nodeList.getLength()-1; i >= 0; i--) {
			element = (org.w3c.dom.Element)nodeList.item(i);
			systemNode.getElement().insertBefore(element, topElement);
			topElement = element;
		}
		return topElement;
	}
	
