	int setOrderOfNewElement(JTable table, org.w3c.dom.Element newElement) {
	    TableRowNumber tableRowNumber;
	    int newRowNumber = 0;
	    int order = 0;
	    if (table.getSelectedRow() == -1) {
	    	if (table.getModel().getRowCount() > 0) {
	    		tableRowNumber = (TableRowNumber)table.getModel().getValueAt(table.getModel().getRowCount() - 1, 0);
	    		org.w3c.dom.Element element = tableRowNumber.getElement();
	    		order = Integer.parseInt(element.getAttribute("Order")) + 10;
	    		newRowNumber = table.getModel().getRowCount();
	    	}
	    } else {
	    	tableRowNumber = (TableRowNumber)table.getModel().getValueAt(table.getSelectedRow(), 0);
	    	order = Integer.parseInt(tableRowNumber.getElement().getAttribute("Order")) + 1;
    		newRowNumber = table.getSelectedRow() + 1;
	    }
		newElement.setAttribute("Order", getFormatted4ByteString(order));
		return newRowNumber;
	}

