	String removeCommentsFromScriptText(String scriptText) {
		StringBuffer buf = new StringBuffer();
		int posCommentStart;
		int posCommentEnd = 0;
		//
		int pos = 0;
		while (pos < scriptText.length()) {
			//
			posCommentStart = scriptText.indexOf("//", pos);
			if (posCommentStart == -1) {
				posCommentStart = scriptText.indexOf("/*", pos);
				if (posCommentStart == -1) {
					posCommentStart = scriptText.length();
					posCommentEnd = scriptText.length();
				} else {
					posCommentEnd = scriptText.indexOf("*/", posCommentStart);
					if (posCommentEnd == -1) {
						posCommentEnd = scriptText.length();
					} else {
						posCommentEnd = posCommentEnd + 2;
					}
				}
			} else {
				posCommentEnd = scriptText.indexOf("\n", posCommentStart);
				if (posCommentEnd == -1) {
					posCommentEnd = scriptText.length();
				} else {
					posCommentEnd = posCommentEnd + 1;
				}
			}
			buf.append(scriptText.substring(pos, posCommentStart));
			//
			pos = posCommentEnd;
		}
		//
		return buf.toString();
	}
	
	boolean containsDataSourceInPhraseValue(String value, String dataSourceName) {
		boolean contains = false;
		ArrayList<String> keywordList = new ArrayList<String>();
		String wrkStr;
		StringTokenizer workTokenizer;
		//
		int pos1 = value.indexOf("&", 0);
		int pos2;
		while (pos1 > -1) {
			pos2 = value.indexOf("&", pos1+1);
			if (pos2 == -1) {
				keywordList.add(value.substring(pos1, value.length()).trim());
				break;
			} else {
				keywordList.add(value.substring(pos1, pos2).trim());
			}
			pos1 = pos2;
		}
		//
		for (int i = 0; i < keywordList.size(); i++) {
			wrkStr = "";
			//
			if (keywordList.get(i).contains("&DataSource(")) {
				pos1 = keywordList.get(i).indexOf(")", 0);
				wrkStr = keywordList.get(i).substring(12, pos1);
			}
			if (keywordList.get(i).contains("&DataSourceImage(")) {
				pos1 = keywordList.get(i).indexOf(")", 0);
				wrkStr = keywordList.get(i).substring(17, pos1);
			}
			if (keywordList.get(i).contains("&Barcode(")) {
				pos1 = keywordList.get(i).indexOf(")", 0);
				wrkStr = keywordList.get(i).substring(9, pos1);
			}
			//
			if (!wrkStr.equals("")) {
				workTokenizer = new StringTokenizer(wrkStr, ";");
				wrkStr = workTokenizer.nextToken().trim();
				if (wrkStr.equals(dataSourceName)) {
					contains = true;
					break;
				}
			}
		}
		//
		return contains;
	}

