	void checkTableModule(MainTreeNode tableNode, boolean isShowDialog) {
		if (isShowDialog) {
			currentMainTreeNode.updateFields();
		}
		String errorStatus = dialogCheckTableModule.request(tableNode, isShowDialog);
		if (!tableNode.getErrorStatus().equals(errorStatus)) {
			tableNode.setErrorStatus(errorStatus);
			repaintErrorStatusOfParentNodes((MainTreeNode)tableNode.getParent());
		}
		if (isShowDialog) {
			currentMainTreeNode.activateContentsPane();
		}
	}
	
	public String checkSyntaxError(String script, boolean isShowResult) {
		String message = "";
		ScriptEngineManager manager = new ScriptEngineManager();
		Compilable engine = (Compilable)manager.getEngineByName("js");
		try {
			engine.compile(script);
		} catch (ScriptException ex) {
			message = ex.getMessage().replaceAll("sun.org.mozilla.javascript.internal.EvaluatorException: " , "");
			message = message.replaceAll("<Unknown Source>" , "");
		}
		if (isShowResult) {
			if (message.equals("")) {
				JOptionPane.showMessageDialog(null, res.getString("ScriptErrorNotFound"));
			} else {
				JOptionPane.showMessageDialog(null, res.getString("ScriptError") + "\n" + message);
			}
		}
		return message;
	}

	
	void repaintErrorStatusOfParentNodes(MainTreeNode tableListNode) {
		MainTreeNode workNode;
		boolean noError = true;
		//
		for (int i = 0; i < tableListNode.getChildCount(); i++) {
			workNode = (MainTreeNode)tableListNode.getChildAt(i);
			if (!workNode.getErrorStatus().equals("")) {
				noError = false;
				break;
			}
		}
		MainTreeNode subsystemNode = (MainTreeNode)tableListNode.getParent();
		if (noError) {
			tableListNode.setErrorStatus("");
			subsystemNode.setErrorStatus("");
		} else {
			tableListNode.setErrorStatus("ERR");
			subsystemNode.setErrorStatus("ERR");
		}
		//
		noError = true;
		MainTreeNode subsystemListNode = (MainTreeNode)subsystemNode.getParent();
		for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
			workNode = (MainTreeNode)subsystemListNode.getChildAt(i);
			if (!workNode.getErrorStatus().equals("")) {
				noError = false;
				break;
			}
		}
		MainTreeNode systemNode = (MainTreeNode)subsystemListNode.getParent();
		if (noError) {
			subsystemListNode.setErrorStatus("");
			systemNode.setErrorStatus("");
		} else {
			subsystemListNode.setErrorStatus("ERR");
			systemNode.setErrorStatus("ERR");
		}
		//
		jTreeMain.repaint();
	}

	
