class Editor_FocusListener implements FocusListener{
	String className;
	public void focusLost(FocusEvent e){
		className = e.getComponent().getClass().getName(); 
		if (className.equals("javax.swing.JTable")) {
			JTable table = (JTable)e.getComponent(); 
			table.setSelectionBackground(Editor.SELECTED_INACTIVE_COLOR);
			table.setSelectionForeground(Color.black);
		}
	}
	public void focusGained(FocusEvent e){
		className = e.getComponent().getClass().getName(); 
		if (className.equals("javax.swing.JTable")) {
			JTable table = (JTable)e.getComponent(); 
			table.setSelectionBackground(Editor.SELECTED_ACTIVE_COLOR);
			table.setSelectionForeground(Color.white);
		}
	}
}

