	public int request(String primaryTableID, String primaryTableName, String batchWithKeyFields, NodeList primaryTableReferList, String batchTableID, String batchKeyFields) {
		org.w3c.dom.Element workElement; 
		NodeList nodeList;
		StringTokenizer workTokenizer1, workTokenizer2;
		//
		batchTableID_ = batchTableID;
		//
		primaryTableID_ = primaryTableID;
		primaryTableReferList_ = primaryTableReferList;
        tableNode = frame_.getSpecificXETreeNode("Table", primaryTableID_);
        primaryTableElement_ = tableNode.getElement();
		primaryTablePKFieldIDList.clear();
        nodeList = primaryTableElement_.getElementsByTagName("Key");
		for (int i = 0; i < nodeList.getLength(); i++) {
			workElement = (org.w3c.dom.Element)nodeList.item(i);
			if (workElement.getAttribute("Type").equals("PK")) {
				workTokenizer1 = new StringTokenizer(workElement.getAttribute("Fields"), ";" );
				while (workTokenizer1.hasMoreTokens()) {
					primaryTablePKFieldIDList.add(workTokenizer1.nextToken());
				}
				break;
			}
		}
		//
		nodeList = primaryTableElement_.getElementsByTagName("Refer");
		jComboBoxBatchTable.removeAllItems();
		refTableIDList.clear();
		jComboBoxBatchTable.addItem("");
		refTableIDList.add("");
		boolean okay;
		for (int i = 0; i < nodeList.getLength(); i++) {
			okay = true;
			workElement = (org.w3c.dom.Element)nodeList.item(i);
			workTokenizer1 = new StringTokenizer(workElement.getAttribute("WithKeyFields"), ";" );
			while (workTokenizer1.hasMoreTokens()) {
				workTokenizer2 = new StringTokenizer(workTokenizer1.nextToken(), "." );
				if (workTokenizer2.nextToken().equals(primaryTableID)) {
					if (primaryTablePKFieldIDList.contains(workTokenizer2.nextToken())) {
						okay = false;
						break;
					}
				} else {
					okay = false;
					break;
				}
			}
			if (okay) {
				tableNode = frame_.getSpecificXETreeNode("Table", workElement.getAttribute("ToTable"));
				jComboBoxBatchTable.addItem(workElement.getAttribute("ToTable") + " " + tableNode.getElement().getAttribute("Name"));
				refTableIDList.add(workElement.getAttribute("ToTable"));
			}
		}
		if (refTableIDList.size() <= 1) {
			jComboBoxBatchTable.setEnabled(false);
		} else {
			jComboBoxBatchTable.setEnabled(true);
			if (batchTableID_.equals("")) {
				jComboBoxBatchTable.setSelectedIndex(0);
			} else {
				int index = refTableIDList.indexOf(batchTableID_);
				if (index > -1) {
					jComboBoxBatchTable.setSelectedIndex(index);
				}
			}
		}
		//
		jTextFieldPrimaryTableName.setText(primaryTableName);
		jTextFieldBatchWithKeyFields.setText(batchWithKeyFields);
		jTextFieldBatchKeyFields.setText(batchKeyFields);
		//
		returnCode = 0;
		jTextAreaMessage.setText(res.getString("BatchKeyAbout"));
		//
		this.setSize(new Dimension(550, 291));
		Dimension dlgSize = this.getSize();
		Dimension frmSize = frame_.getSize();
		Point loc = frame_.getLocation();
		this.setLocation((frmSize.width - dlgSize.width) / 2 + loc.x, (frmSize.height - dlgSize.height) / 2 + loc.y);
		this.getRootPane().setDefaultButton(jButtonOK);
		super.setVisible(true);
		//
		return returnCode;
	}
