class Editor_NumberEditorFormatter extends InternationalFormatter {
	private static final long serialVersionUID = 1L;
	private final SpinnerNumberModel model;

	Editor_NumberEditorFormatter(SpinnerNumberModel model,NumberFormat format) {
		super(format);
		this.model = model;
		setValueClass(model.getValue().getClass());
	}

	@SuppressWarnings("rawtypes")
	public Comparable getMaximum() {
		return model.getMaximum();
	}
	@SuppressWarnings("rawtypes")
	public Comparable getMinimum() {
		return model.getMinimum();
	}
	@SuppressWarnings("rawtypes")
	public void setMaximum(Comparable max) {
		model.setMaximum(max);
	}
	@SuppressWarnings("rawtypes")
	public void setMinimum(Comparable min) {
		model.setMinimum(min);
	}

	@SuppressWarnings("unchecked")
	public Object stringToValue(String text) throws ParseException {
		try {
			return super.stringToValue(text);
		} catch (ParseException e) {
			Object object = getFormat().parseObject(text);
			int num;
			if (object instanceof Integer) {
				num = (Integer)object;
			} else if (object instanceof Number) {
				num = new Integer(((Number)object).intValue());
			} else {
				throw e;
			}
			if (getMaximum().compareTo(num) < 0) {
				return getMaximum();
			} else if (getMinimum().compareTo(num) > 0) {
				return getMinimum();
			} else {
				throw e;
			}
		}
	}
}


class XEditor_FieldSizeSpinnerEditor extends JSpinner.NumberEditor {
	private static final long serialVersionUID = 1L;
	public XEditor_FieldSizeSpinnerEditor(JSpinner spinner, String decimalFormatPattern) {
       super(spinner, decimalFormatPattern);
       Editor_NumberEditorFormatter formatter = new Editor_NumberEditorFormatter((SpinnerNumberModel)spinner.getModel(), new DecimalFormat(decimalFormatPattern));
       DefaultFormatterFactory factory = new DefaultFormatterFactory(formatter);
       getTextField().setFormatterFactory(factory);
    }

    public void commitEdit() throws ParseException {
       Object old = getModel().getValue();
       String newStr = getTextField().getText();

       try {
          AbstractFormatter formatter = getTextField().getFormatter();
          String oldStr = formatter.valueToString(old);

          if (newStr.startsWith(oldStr)) {
             return;
          }
          super.commitEdit();
       } catch (ParseException e) {
       }
    }
}

