	public String getStringValueOfDateTime(String timeFormat) {
		String monthStr = "";
		String dayStr = "";
		String hourStr = "";
		String minStr = "";
		String secStr = "";
		String returnValue = "";
		GregorianCalendar calendar = new GregorianCalendar();
		//
		int year = calendar.get(Calendar.YEAR);
		//
		int month = calendar.get(Calendar.MONTH) + 1;
		if (month < 10) {
			monthStr = "0" + Integer.toString(month);
		} else {
			monthStr = Integer.toString(month);
		}
		//
		int day = calendar.get(Calendar.DAY_OF_MONTH);
		if (day < 10) {
			dayStr = "0" + Integer.toString(day);
		} else {
			dayStr = Integer.toString(day);
		}
		//
		if (timeFormat.equals("withTime")) {
			int hour = calendar.get(Calendar.HOUR_OF_DAY);
			if (hour < 10) {
				hourStr = "0" + Integer.toString(hour);
			} else {
				hourStr = Integer.toString(hour);
			}
			//
			int minute = calendar.get(Calendar.MINUTE);
			if (minute < 10) {
				minStr = "0" + Integer.toString(minute);
			} else {
				minStr = Integer.toString(minute);
			}
			//
			int second = calendar.get(Calendar.SECOND);
			if (second < 10) {
				secStr = "0" + Integer.toString(second);
			} else {
				secStr = Integer.toString(second);
			}
		}
		//
		returnValue = Integer.toString(year) + monthStr + dayStr + hourStr + minStr + secStr;
		return returnValue;
	}
	
