	MainTreeNode getSpecificXETreeNode(String type, String id1) {
		int childCount1;
		MainTreeNode returnNode = null;
		MainTreeNode childNode1, childNode2, childNode3;
		//
		if (type.equals("Menu")) {
			for (int i = 0; i < menuListNode.getChildCount(); i++) {
				childNode1 = (MainTreeNode)menuListNode.getChildAt(i);
				if (childNode1.getElement().getAttribute("ID").equals(id1)) {
					returnNode = childNode1;
					break;
				}
			}
		}
		//
		if (type.equals("Subsystem")) {
			for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
				childNode1 = (MainTreeNode)subsystemListNode.getChildAt(i);
				if (childNode1.getElement().getAttribute("ID").equals(id1)) {
					returnNode = childNode1;
					break;
				}
			}
		}
		//
		if (type.equals("Function")) {
			breakTag:
				for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
					childNode1 = (MainTreeNode)subsystemListNode.getChildAt(i);
					childNode2 = (MainTreeNode)childNode1.getChildAt(1); //SubsystemFunctionList//
					childCount1 = childNode2.getChildCount(); //Number of Subsystem Functions//
					for (int j = 0; j < childCount1; j++) {
						childNode3 = (MainTreeNode)childNode2.getChildAt(j);
						if (childNode3.getElement().getAttribute("ID").equals(id1)) {
							returnNode = childNode3;
							break breakTag;
						}
					}
				}
		}
		//
		if (type.equals("Table")) {
			breakTag:
				for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
					childNode1 = (MainTreeNode)subsystemListNode.getChildAt(i);
					childNode2 = (MainTreeNode)childNode1.getChildAt(0); //SubsystemTableList//
					childCount1 = childNode2.getChildCount(); //Number of Subsystem Tables//
					for (int j = 0; j < childCount1; j++) {
						childNode3 = (MainTreeNode)childNode2.getChildAt(j);
						if (childNode3.getElement().getAttribute("ID").equals(id1)) {
							returnNode = childNode3;
							break breakTag;
						}
					}
				}
		}
		//
		return returnNode;
	}

	org.w3c.dom.Element getSpecificFieldElement(String tableID, String fieldID) {
		org.w3c.dom.Element returnElement = null;
		org.w3c.dom.Element element;
		//
		MainTreeNode tableNode = getSpecificXETreeNode("Table", tableID);
		if (tableNode != null) {
			NodeList fieldList = tableNode.getElement().getElementsByTagName("Field");
			for (int i = 0; i < fieldList.getLength(); i++) {
				element = (org.w3c.dom.Element)fieldList.item(i);
				if (element.getAttribute("ID").equals(fieldID)) {
					returnElement = element;
				}
			}
		}
		//
		return returnElement;
	}

	org.w3c.dom.Element getSpecificPKElement(String tableID) {
		org.w3c.dom.Element returnElement = null;
		org.w3c.dom.Element element;
		//
		MainTreeNode tableNode = getSpecificXETreeNode("Table", tableID);
		NodeList keyList = tableNode.getElement().getElementsByTagName("Key");
		for (int i = 0; i < keyList.getLength(); i++) {
			element = (org.w3c.dom.Element)keyList.item(i);
			if (element.getAttribute("Type").equals("PK")) {
				returnElement = element;
			}
		}
		//
		return returnElement;
	}
	
