		public void addChildNode(MainTreeNode childNode, String nodeID, String nodeName) {
			MainTreeNode childNode1 = null;
			MainTreeNode childNode2 = null;
			MainTreeNode childNode3 = null;
			org.w3c.dom.Element workElement;
			org.w3c.dom.Element newElement = null;
			int childCount1;
			String id1, id2;
			boolean duplicated;
			//
			if (childNode == null) {
				if (nodeType_.equals("MenuList")) {
					duplicated = false;
					for (int i = 0; i < this.getChildCount(); i++) {
						workElement = ((MainTreeNode)this.getChildAt(i)).getElement();
						if (workElement.getAttribute("ID").equals(nodeID)) {
							duplicated = true;
							break;
						}
					}
					if (duplicated) {
						JOptionPane.showMessageDialog(null, res.getString("ErrorMessage16") + nodeID + res.getString("ErrorMessage17"));
					} else {
						newElement = domDocument.createElement("Menu");
						newElement.setAttribute("ID", nodeID);
						newElement.setAttribute("Name", nodeName);
						newElement.setAttribute("HelpURL", "");
						childNode = new MainTreeNode("Menu", newElement, this.getEditor());
						systemNode.getElement().appendChild(newElement);
					}
				}
				//
				if (nodeType_.equals("SubsystemList")) {
					duplicated = false;
					for (int i = 0; i < this.getChildCount(); i++) {
						workElement = ((MainTreeNode)this.getChildAt(i)).getElement();
						if (workElement.getAttribute("ID").equals(nodeID)) {
							duplicated = true;
							break;
						}
					}
					if (duplicated) {
						JOptionPane.showMessageDialog(null, res.getString("ErrorMessage16") + nodeID + res.getString("ErrorMessage17"));
					} else {
						newElement = domDocument.createElement("Subsystem");
						newElement.setAttribute("ID", nodeID);
						newElement.setAttribute("Name", nodeName);
						newElement.setAttribute("Remarks", "");
						systemNode.getElement().appendChild(newElement);
						//
						childNode = new MainTreeNode("Subsystem", newElement, this.getEditor());
						childNode1 = new MainTreeNode("TableList", null, this.getEditor());
						childNode.add(childNode1);
						childNode2 = new MainTreeNode("FunctionList", null, this.getEditor());
						childNode.add(childNode2);
					}
				}
				//
				if (nodeType_.equals("TableList")) {
					duplicated = false;
					for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
						childNode1 = (MainTreeNode)subsystemListNode.getChildAt(i);
						childNode2 = (MainTreeNode)childNode1.getChildAt(0); //SubsystemTableList//
						childCount1 = childNode2.getChildCount(); //Number of Subsystem Tables//
						for (int j = 0; j < childCount1; j++) {
							childNode3 = (MainTreeNode)childNode2.getChildAt(j);
							id1 = childNode3.getElement().getAttribute("ID").toUpperCase();
							id2 = nodeID.toUpperCase();
							if (id1.equals(id2)) {
								duplicated = true;
								break;
							}
						}
						if (duplicated) {
							break;
						}
					}
					if (duplicated) {
						JOptionPane.showMessageDialog(null, res.getString("ErrorMessage18") + nodeID + res.getString("ErrorMessage19"));
					} else {
						String subsystemID = ((MainTreeNode)currentMainTreeNode.getParent()).getElement().getAttribute("ID");
						newElement = domDocument.createElement("Table");
						newElement.setAttribute("ID", nodeID);
						newElement.setAttribute("Name", nodeName);
						newElement.setAttribute("SubsystemID", subsystemID);
						newElement.setAttribute("ActiveWhere", "");
						newElement.setAttribute("RangeKey", "");
						newElement.setAttribute("Remarks", "");
						systemNode.getElement().appendChild(newElement);
						//
						childNode = new MainTreeNode("Table", newElement, this.getEditor());
						childNode.setErrorStatus("ER1");
					}
				}
				//
				//Add Function//
				if (nodeType_.equals("FunctionList")) {
					String subsystemID = ((MainTreeNode)currentMainTreeNode.getParent()).getElement().getAttribute("ID");
					int reply = dialogAddFunction.request(subsystemID);
					if (reply == 1) {
						newElement = dialogAddFunction.getNewElement();
						systemNode.getElement().appendChild(newElement);
						childNode = new MainTreeNode("Function", newElement, this.getEditor());
					}
				}
			} else {
				systemNode.getElement().appendChild(childNode.getElement());
			}
			//
			//Add Log of Add//
			if (childNode != null) {
				this.add(childNode);
				undoManager.addLogOfAdd(childNode);
				//
				if (nodeType_.equals("TableList")) {
					repaintErrorStatusOfParentNodes(this);
				}
			}
			//
			//Sort and refresh page//
			this.sortChildNodes();
			jTreeMain.updateUI();
			activateContentsPane();
		}
		//
		public String deleteNode() {
			MainTreeNode  parentNode = (MainTreeNode)this.getParent();
			String errorMessage = "";
			org.w3c.dom.Element element1;
			NodeList nodeList1;
			//
			//Check error to delete Menu//
			if (nodeType_.equals("Menu")) {
				errorMessage = "";
			}
			//
			//Check error to delete Subsystem//
			if (nodeType_.equals("Subsystem")) {
				String subsystemID = domNode_.getAttribute("ID");
				nodeList1 = domDocument.getElementsByTagName("Table");
				for (int i = 0; i < nodeList1.getLength(); i++) {
					element1 = (org.w3c.dom.Element)nodeList1.item(i);
					if (element1.getAttribute("SubsystemID").equals(subsystemID)) {
						errorMessage = res.getString("ErrorMessage20");
						break;
					}
				}
				if (errorMessage.equals("")) {
					nodeList1 = domDocument.getElementsByTagName("Function");
					for (int i = 0; i < nodeList1.getLength(); i++) {
						element1 = (org.w3c.dom.Element)nodeList1.item(i);
						if (element1.getAttribute("SubsystemID").equals(subsystemID)) {
							errorMessage = res.getString("ErrorMessage20");
							break;
						}
					}
				}
			}
			//
			//Check error to delete Table//
			if (nodeType_.equals("Table")) {
				if (tableModelTableUsageList.getRowCount() == 0) {
					if (connection != null && !errorStatus_.equals("ER1")) {
						Object[] bts = {res.getString("Cancel"), res.getString("DeleteTableDefinitionOnly"), res.getString("DeleteTableDefinitionAndModule")};
						int rtn = JOptionPane.showOptionDialog(null, res.getString("DeleteTableMessage"),
								res.getString("DeleteTableModule"), JOptionPane.DEFAULT_OPTION, JOptionPane.INFORMATION_MESSAGE, null, bts, bts[0]);
						//
						if (rtn == 2) {
							dialogSynchTableModule.deleteTable(connection, domNode_.getAttribute("ID"));
						}
						if (rtn != 1 && rtn != 2) {
							errorMessage = res.getString("DeleteCanceled");
						}
					}
				} else {
					errorMessage = res.getString("ErrorMessage21");
				}
			}
			//
			//Check error to delete Function//
			if (nodeType_.equals("Function")) {
				errorMessage = getErrorToDeleteFunction(domNode_.getAttribute("ID"));
			}
			//
			//Delete the node and sort sibling nodes and refresh view//
			if (errorMessage == "") {
				//
				//Add Log of Remove//
				undoManager.addLogOfRemove(this);
				//
				//Remove node//
				systemNode.getElement().removeChild(domNode_);
				parentNode.remove(this);
				if (nodeType_.equals("Table")) {
					repaintErrorStatusOfParentNodes(parentNode);
				}
				//
				setupJumpButtons();
				jTreeMain.updateUI();
			}
			//
			return errorMessage;
		}
		//
		public void pasteChildNode(org.w3c.dom.Element pastingElement) {
			MainTreeNode childNode = null;
			NodeList nodeList;
			org.w3c.dom.Element newElement, workElement;
			boolean duplicated, ready;
			String answer;
			//
			//Paste Menu//
			if (nodeType_.equals("MenuList") && pastingElement.getTagName().equals("Menu")) {
				newElement = (org.w3c.dom.Element)pastingElement.cloneNode(true);
				answer = newElement.getAttribute("ID");
				ready = false;
				while (!ready) {
					answer = JOptionPane.showInputDialog(null, res.getString("SpecifyNewID"), answer);
					if (answer == null || answer.equals("")) {
						ready = true;
					} else {
						duplicated = false;
						for (int i = 0; i < this.getChildCount(); i++) {
							workElement = ((MainTreeNode)this.getChildAt(i)).getElement();
							if (workElement.getAttribute("ID").equals(answer)) {
								duplicated = true;
								break;
							}
						}
						if (duplicated) {
							JOptionPane.showMessageDialog(null, res.getString("ErrorMessage16") + answer + res.getString("ErrorMessage17"));
						} else {
							newElement.setAttribute("ID", answer);
							newElement.setAttribute("Name", newElement.getAttribute("Name") + res.getString("CopiedNameExt"));
							systemNode.getElement().appendChild(newElement);
							childNode = new MainTreeNode("Menu", newElement, this.getEditor());
							this.add(childNode);
							ready = true;
						}
					}
				}
			}
			//
			//Paste Table//
			if (nodeType_.equals("TableList") && pastingElement.getTagName().equals("Table")) {
				newElement = (org.w3c.dom.Element)pastingElement.cloneNode(false);
				nodeList = pastingElement.getElementsByTagName("Field");
				for (int i = 0; i < nodeList.getLength(); i++) {
					newElement.appendChild(nodeList.item(i).cloneNode(false));
				}
				nodeList = pastingElement.getElementsByTagName("Key");
				for (int i = 0; i < nodeList.getLength(); i++) {
					newElement.appendChild(nodeList.item(i).cloneNode(false));
				}
				answer = newElement.getAttribute("ID");
				ready = false;
				while (!ready) {
					answer = JOptionPane.showInputDialog(null, res.getString("SpecifyNewIDOfTable"), answer);
					if (answer == null || answer.equals("")) {
						ready = true;
					} else {
						if (answer.contains("_")) {
							JOptionPane.showMessageDialog(null, res.getString("ErrorMessage105"));
							ready = true;
						} else {
							duplicated = false;
							for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
								for (int j = 0; j < subsystemListNode.getChildAt(i).getChildAt(0).getChildCount(); j++) {
									workElement = ((MainTreeNode)subsystemListNode.getChildAt(i).getChildAt(0).getChildAt(j)).getElement();
									if (workElement.getAttribute("ID").equals(answer)) {
										duplicated = true;
										break;
									}
								}
								if (duplicated) {
									break;
								}
							}
							if (duplicated) {
								JOptionPane.showMessageDialog(null, res.getString("ErrorMessage16") + answer + res.getString("ErrorMessage17"));
							} else {
								newElement.setAttribute("ID", answer);
								newElement.setAttribute("Name", newElement.getAttribute("Name") + res.getString("CopiedNameExt"));
								String subsystemID = ((MainTreeNode)this.getParent()).getElement().getAttribute("ID");
								newElement.setAttribute("SubsystemID", subsystemID);
								systemNode.getElement().appendChild(newElement);
								childNode = new MainTreeNode("Table", newElement, this.getEditor());
								childNode.setErrorStatus("ER1");
								this.add(childNode);
								ready = true;
							}
						}
					}
				}
			}
			//
			//Paste Function//
			if (nodeType_.equals("FunctionList") && pastingElement.getTagName().equals("Function")) {
				newElement = (org.w3c.dom.Element)pastingElement.cloneNode(true);
				answer = newElement.getAttribute("ID");
				ready = false;
				while (!ready) {
					answer = JOptionPane.showInputDialog(null, res.getString("SpecifyNewID"), answer);
					if (answer == null || answer.equals("")) {
						ready = true;
					} else {
						duplicated = false;
						for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
							for (int j = 0; j < subsystemListNode.getChildAt(i).getChildAt(1).getChildCount(); j++) {
								workElement = ((MainTreeNode)subsystemListNode.getChildAt(i).getChildAt(1).getChildAt(j)).getElement();
								if (workElement.getAttribute("ID").equals(answer)) {
									duplicated = true;
									break;
								}
							}
							if (duplicated) {
								break;
							}
						}
						if (duplicated) {
							JOptionPane.showMessageDialog(null, res.getString("ErrorMessage16") + answer + res.getString("ErrorMessage17"));
						} else {
							newElement.setAttribute("ID", answer);
							newElement.setAttribute("Name", newElement.getAttribute("Name") + res.getString("CopiedNameExt"));
							String subsystemID = ((MainTreeNode)this.getParent()).getElement().getAttribute("ID");
							newElement.setAttribute("SubsystemID", subsystemID);
							systemNode.getElement().appendChild(newElement);
							childNode = new MainTreeNode("Function", newElement, this.getEditor());
							this.add(childNode);
							ready = true;
						}
					}
				}
			}
			//
			//Add Log of Add//
			if (childNode != null) {
				undoManager.addLogOfAdd(childNode);
				//
				if (nodeType_.equals("TableList")) {
					repaintErrorStatusOfParentNodes(this);
				}
			}
			//
			//Sort nodes and refresh view//
			this.sortChildNodes();
			jTreeMain.updateUI();
			this.activateContentsPane();
		}

		public void sortChildNodes() {
			TreeSet<MainTreeNode> treeSet = new TreeSet<MainTreeNode>(new NodeComparator());
			int childCount = this.getChildCount();
			MainTreeNode node;
			for (int i = 0; i < childCount; i++) {
				node = (MainTreeNode)this.getChildAt(i);
				treeSet.add(node);
			}
			this.removeAllChildren();
			Iterator<MainTreeNode> it = treeSet.iterator();
			while( it.hasNext() ){
				node = (MainTreeNode)it.next();
				this.add(node);
			}
		}

