	/**
	 * Method to concat lines into a single string value with EOL mark
	 * @param originalString :original string value to be processed
	 * @return String :string value of processed string
	 */
	public String concatLinesWithTokenOfEOL(String originalString) {
		StringBuffer processedString = new StringBuffer();
		int lastEnd = 0;
		for (int i = 0; i <= originalString.length(); i++) {
			if (i < originalString.length()) {
				if (originalString.substring(i,i+1).equals("\n")) {
					processedString.append(originalString.substring(lastEnd, i));
					processedString.append("#EOL#");
					lastEnd = i+1;
				}
			} else {
				processedString.append(originalString.substring(lastEnd, i));
			}
		}
		return processedString.toString();
	}

	public void pasteTextToIndent(JScrollPane scrollPane) {
		/////////////////////////////////////////////
		// Get text data from the system clipboard //
		/////////////////////////////////////////////
		String clipboardData = "";
		Clipboard clipboard = getToolkit().getSystemClipboard();
		Transferable contentsReceived = clipboard.getContents(this);
		if (contentsReceived != null) {
			if (contentsReceived.isDataFlavorSupported(DataFlavor.stringFlavor)) {
				try {
					clipboardData = (String)contentsReceived.getTransferData(DataFlavor.stringFlavor);
				} catch (Exception ex) {}
			}
		}
		//
		JTextArea textArea = (JTextArea)scrollPane.getViewport().getView();
		if (textArea.getSelectionStart() < textArea.getSelectionEnd()) {
			/////////////////////////////////////////////
			// Replace selected text to clipboard text //
			/////////////////////////////////////////////
			String text = textArea.getText();
			int selectFrom = textArea.getSelectionStart();
			int selectThru = textArea.getSelectionEnd();
			StringBuffer buf = new StringBuffer();
			buf.append(text.substring(0, selectFrom));
			buf.append(clipboardData);
			buf.append(text.substring(selectThru, text.length()));
			textArea.setText(buf.toString());
			textArea.select(0, 0);
			try {
				textArea.setCaretPosition(selectFrom + clipboardData.length());
			} catch (Exception e) {
			}
			
		} else {
			///////////////////////////////////////////////////////
			// Insert clipboard text after caret and indent them //
			///////////////////////////////////////////////////////
			int caretPos = textArea.getCaretPosition();
			String text = textArea.getText();
			StringBuffer buf = new StringBuffer();
			buf.append(text.substring(0, caretPos));
			buf.append(clipboardData);
			buf.append(text.substring(caretPos, text.length()));
			textArea.setText(buf.toString());
			textArea.setSelectionStart(caretPos);
			textArea.setSelectionEnd(caretPos + clipboardData.length());
			indentRows(scrollPane);
		}
	}
	
	public void indentRows(JScrollPane scrollPane) {
		StringBuffer buf = new StringBuffer();
		String strOriginal, wrkStr1, wrkStr2;
		int pos1 = 0;
		int pos2 = 0;
		int countOfIndentsToBeAdded = 0;
		int countOfRows = 0;
		int countOfIndentsToBeRemoved = 0;
		int wrkInt, countAdjustment = 0;
		boolean isFirstRowOfStatement = true;
		boolean isCommentRow = false;
		int selectionRowFrom = 0;
		int selectionRowThru = 0;
		boolean isWithinSelection = false;
		JTextArea textArea = (JTextArea)scrollPane.getViewport().getView();
		//
		//////////////////////////////////////////////////
		// Calculate row numbers of selection start/end //
		//////////////////////////////////////////////////
		wrkInt = 0;
		while (pos2 < (textArea.getText().length() - 1)) {
			pos2 = textArea.getText().indexOf("\n", pos1);
			if (pos2 == -1) {
				pos2 = textArea.getText().length() -1;
			}
			if (pos2 >= textArea.getSelectionStart() && pos2 <= textArea.getSelectionEnd()) {
				selectionRowThru = wrkInt;
				if (!isWithinSelection) {
					selectionRowFrom = wrkInt;
					isWithinSelection = true;
				}
			}
			pos1 = pos2+1;
			wrkInt++;
		}
		//
		///////////////////////////////////////////////////////////
		// Process rows and indent them if it's within selection //
		///////////////////////////////////////////////////////////
		pos1 = 0; pos2 = 0;
		while (pos2 >= 0) {
			pos2 = textArea.getText().indexOf("\n", pos1);
			if (pos2 >= 0) {
				strOriginal = textArea.getText().substring(pos1, pos2+1);
			} else {
				strOriginal = textArea.getText().substring(pos1);
			}
			//
			wrkStr1 = strOriginal;
			wrkStr2 = wrkStr1;
			if (isFirstRowOfStatement) {
				countOfIndentsToBeRemoved = 0;
				for (int i = 0; i < wrkStr1.length(); i++) {
					if (wrkStr1.substring(i, i+1).equals("\t")) {
						countOfIndentsToBeRemoved++;
						wrkStr2 = wrkStr2.replaceFirst("\t", "");
					}
				}
			} else {
				for (int i = 0; i < countOfIndentsToBeRemoved; i++) {
					wrkStr2 = wrkStr2.replaceFirst("\t", "");
				}
			}
			wrkStr1 = wrkStr2;
			//
			isCommentRow = false;
			for (int i = 0; i < wrkStr1.length(); i++) {
				if (!wrkStr1.substring(i, i+1).equals("\t") && !wrkStr1.substring(i, i+1).equals(" ")) {
					wrkInt = i + 1;
					if (wrkInt < wrkStr1.length() && wrkStr1.substring(i, i+2).equals("//")) {
						isCommentRow = true;
						break;
					}
				}
			}
			if (isCommentRow || wrkStr1.equals("\n")) {
				isFirstRowOfStatement = true;
			} else {
				if (wrkStr1.contains(";") || wrkStr1.contains("{") || wrkStr1.contains("}")) {
					isFirstRowOfStatement = true;
				} else {
					isFirstRowOfStatement = false;
				}
			}
			//
			pos1 = pos2+1;
			//
			if (wrkStr1.contains("}")) {
				countOfIndentsToBeAdded--;
			}
			//
			if (!wrkStr1.equals("\n")) {
				for (int i = 0; i < countOfIndentsToBeAdded; i++) {
					wrkStr1 = "\t" + wrkStr1;
				}
			}
			//
			if (countOfRows >= selectionRowFrom && countOfRows <= selectionRowThru) {
				countAdjustment = countAdjustment - countOfIndentsToBeRemoved;
				countAdjustment = countAdjustment + countOfIndentsToBeAdded;
				buf.append(wrkStr1);
			} else {
				buf.append(strOriginal);
			}
			//
			if (wrkStr1.contains("{")) {
				countOfIndentsToBeAdded++;
			}
			//
			countOfRows++;
		}
		//
		Point point = scrollPane.getViewport().getViewPosition();
		int caretPos = textArea.getCaretPosition();
		textArea.setText(buf.toString());
		textArea.select(0, 0);
		try {
			textArea.setCaretPosition(caretPos + countAdjustment);
		} catch (Exception e) {
		}
		scrollPane.getViewport().setViewPosition(point);
	}

	/**
	 * Method to get first sentence from specified string
	 * @param originalString :original value of string
	 * @return String :string value of first sentence
	 */
	public String getFirstSentence(String originalString) {
		String sentence = "";
		originalString = substringLinesWithTokenOfEOL(originalString, "  ");
		if (originalString.length() >= 2) {
			for (int i = 0; i < originalString.length(); i++) {
				if (i+2 <= originalString.length()) {
					if (originalString.substring(i,i+1).equals("B")) {
						sentence = originalString.substring(0, i) + "...";
						break;
					}
					if (originalString.substring(i,i+2).equals(". ")) {
						sentence = originalString.substring(0, i+1) + "...";
						break;
					}
					if (originalString.substring(i,i+2).equals("  ")) {
						sentence = originalString.substring(0, i+1) + "...";
						break;
					}
				} else {
					if (originalString.substring(originalString.length()-1,originalString.length()).equals("B")) {
						sentence = originalString.substring(0, originalString.length()-1);
						break;
					} else {
						sentence = originalString;
						break;
					}
				}
			}
		} else {
			sentence = originalString;
		}
		return sentence;
	}

	private String getOperandType(String options) {
		String operandType = "";
		if (options.length() >= 2 && options.substring(0, 2).equals("EQ")) {
			operandType = "";
		}
		if (options.length() >= 2 && options.substring(0, 2).equals("GE")) {
			operandType = "GE";
		}
		if (options.length() >= 2 && options.substring(0, 2).equals("GT")) {
			operandType = "GT";
		}
		if (options.length() >= 2 && options.substring(0, 2).equals("LE")) {
			operandType = "LE";
		}
		if (options.length() >= 2 && options.substring(0, 2).equals("LT")) {
			operandType = "LT";
		}
		if (options.length() >= 4 && options.substring(0, 4).equals("SCAN")) {
			operandType = "SCAN";
		}
		if (options.length() >= 7 && options.substring(0, 7).equals("GENERIC")) {
			operandType = "GENERIC";
		}
		if (options.length() >= 7 && options.substring(0, 7).equals("REFLECT")) {
			operandType = "REFLECT";
		}
		return operandType;
	}
	
	private String getDescriptionsOfTypeAndSize(String dataType, String dataSize, String decimal) {
		String descriptions = "";
		if (isWithDecimal(dataType)) {
			descriptions = dataType + "(" + dataSize + "," + decimal + ") ";
		} else {
			if (dataType.equals("CHAR")
					|| dataType.equals("VARCHAR")
					|| dataType.equals("INTEGER")
					|| dataType.equals("SMALLINT")
					|| dataType.equals("BIGINT")) {
				descriptions = dataType + "(" + dataSize + ") ";
			} else {
				descriptions = dataType;
			}
		}
		return descriptions + " ";
	}
	
	boolean isWithDecimal(String dataType) {
		boolean result = false;
		if (dataType.equals("DOUBLE")
				|| dataType.equals("DECIMAL")
				|| dataType.equals("FLOAT")
				|| dataType.equals("DOUBLE PRECISION")
				|| dataType.equals("NUMERIC")
				|| dataType.equals("REAL")	) {
			result = true;
		}
		return result;
	}
	
	boolean isNumeric(String dataType) {
		boolean result = false;
		if (dataType.equals("INTEGER")
				|| dataType.equals("SMALLINT")
				|| dataType.equals("BIGINT")
				|| dataType.equals("DOUBLE")
				|| dataType.equals("DECIMAL")
				|| dataType.equals("FLOAT")
				|| dataType.equals("DOUBLE PRECISION")
				|| dataType.equals("NUMERIC")
				|| dataType.equals("REAL")) {
			result = true;
		}
		return result;
	}

