	org.w3c.dom.Element createNewElementAccordingToType(String type) {
		org.w3c.dom.Element newElement = null;
		org.w3c.dom.Element element;
		TableRowNumber tableRowNumber;
		int wrkInt, lastOrder = 0;
		//
		informationOnThisPageChanged = true;
		//
		if (type.equals("SystemPrintFontList")) {
			newElement = domDocument.createElement("PrintFont");
			if (tableModelSystemPrintFontList.getRowCount() > 0) {
			    for (int i = 0; i < tableModelSystemPrintFontList.getRowCount(); i++) {
			    	tableRowNumber = (TableRowNumber)tableModelSystemPrintFontList.getValueAt(i, 0);
			    	element = tableRowNumber.getElement();
			    	wrkInt = Integer.parseInt(element.getAttribute("ID").substring(4, element.getAttribute("ID").length()));
			    	if (wrkInt > lastOrder) {
			    		lastOrder = wrkInt;
			    	}
			    }
				lastOrder = lastOrder + 1;
				newElement.setAttribute("ID", "Font" + lastOrder);
			} else {
				newElement.setAttribute("ID", "Font0");
			}
			newElement.setAttribute("FontName", newElement.getAttribute("ID"));
			newElement.setAttribute("PDFFontName", "Times-Roman");
			newElement.setAttribute("PDFEncoding", "Cp1252");
		}
		//
		if (type.equals("TableFieldList")) {
			newElement = domDocument.createElement("Field");
			if (tableModelTableFieldList.getRowCount() > 0) {
				tableRowNumber = (TableRowNumber)tableModelTableFieldList.getValueAt(tableModelTableFieldList.getRowCount() - 1, 0);
				element = tableRowNumber.getElement();
				lastOrder = Integer.parseInt(element.getAttribute("Order"));
				lastOrder = lastOrder + 10;
				newElement.setAttribute("Order", getFormatted4ByteString(lastOrder));
			} else {
				newElement.setAttribute("Order", "0000");
			}
			newElement.setAttribute("Type", "CHAR");
			newElement.setAttribute("Size", "5");
			newElement.setAttribute("Nullable", "F");
			newElement.setAttribute("TypeOptions", "");
		}
		//
		if (type.equals("TableKeyList")) {
			newElement = domDocument.createElement("Key");
		}
		//
		if (type.equals("TableScriptList")) {
			newElement = domDocument.createElement("Script");
			if (tableModelTableScriptList.getRowCount() > 0) {
				tableRowNumber = (TableRowNumber)tableModelTableScriptList.getValueAt(tableModelTableScriptList.getRowCount() - 1, 0);
				element = tableRowNumber.getElement();
				lastOrder = Integer.parseInt(element.getAttribute("Order"));
				lastOrder = lastOrder + 10;
				newElement.setAttribute("Order", getFormatted4ByteString(lastOrder));
			} else {
				newElement.setAttribute("Order", "0000");
			}
			newElement.setAttribute("EventP", "BR");
			newElement.setAttribute("EventR", "");
			newElement.setAttribute("Function", "");
			newElement.setAttribute("Name", res.getString("NewScript"));
			newElement.setAttribute("Text", "");
		}
		//
		if (type.equals("Function100ButtonList") || 
				type.equals("Function110ButtonList") ||
				type.equals("Function200ButtonList") ||
				type.equals("Function210ButtonList") ||
				type.equals("Function300DetailButtonList") ||
				type.equals("Function310ButtonList")) {
			newElement = domDocument.createElement("Button");
			newElement.setAttribute("Position", "6");
			newElement.setAttribute("Number", "3");
			newElement.setAttribute("Caption", res.getString("Exit"));
			newElement.setAttribute("Action", "EXIT");
		}
		//
		if (type.equals("Function310AddRowListButtonList")) {
			newElement = domDocument.createElement("AddRowListButton");
			newElement.setAttribute("Position", "5");
			newElement.setAttribute("Number", "3");
			newElement.setAttribute("Caption", res.getString("Exit"));
			newElement.setAttribute("Action", "EXIT");
		}
		//
		return newElement;
	}

