	String getFormatted4ByteString(int number) {
		String formattedNumberString = "0000";
		if (0 <= number && number < 10) {formattedNumberString = "000" + number;}
		if (10 <= number && number < 100) {formattedNumberString = "00" + number;}
		if (100 <= number && number < 1000) {formattedNumberString = "0" + number;}
		if (1000 <= number && number < 10000) {formattedNumberString = Integer.toString(number);}
		return formattedNumberString;
	}
	
	boolean isValidDraggingOfRow(JTable table, int objectRow, int targetRow) {
		boolean result = true;
		//
		if (table == jTableTableReferList) {
			TableRowNumber tableRowNumber;
			TableRowNumber objectRowNumber = null;
			org.w3c.dom.Element element;
			String tableID;
			String wrkStr;
			StringTokenizer workTokenizer;
			ArrayList<String> dataSourceList = new ArrayList<String>();
			ArrayList<TableRowNumber> rowNumberList = new ArrayList<TableRowNumber>();
			boolean keyFieldsAreOnTheList;
			//
			tableID = currentMainTreeNode.getElement().getAttribute("ID");
			NodeList nodeList1 = currentMainTreeNode.getElement().getElementsByTagName("Field");
		    for (int i = 0; i < nodeList1.getLength(); i++) {
		    	element = (org.w3c.dom.Element)nodeList1.item(i);
		    	dataSourceList.add(tableID + "." + element.getAttribute("ID"));
		    }
		    //
			for (int i = 0; i < tableModelTableReferList.getRowCount(); i++) {
				if (i == objectRow) {
					objectRowNumber = (TableRowNumber)tableModelTableReferList.getValueAt(i, 0);
				}
			}
			for (int i = 0; i < tableModelTableReferList.getRowCount(); i++) {
				tableRowNumber = (TableRowNumber)tableModelTableReferList.getValueAt(i, 0);
				if (i != objectRow) {
					rowNumberList.add(tableRowNumber);
				}
				if (i == targetRow) {
					rowNumberList.add(objectRowNumber);
				}
			}
			for (int i = 0; i < rowNumberList.size(); i++) {
				tableRowNumber = rowNumberList.get(i);
				keyFieldsAreOnTheList = true;
				element = tableRowNumber.getElement();
				workTokenizer = new StringTokenizer(element.getAttribute("WithKeyFields"), ";" );
				while (workTokenizer.hasMoreTokens()) {
					wrkStr = workTokenizer.nextToken();
					if (!dataSourceList.contains(wrkStr)) {
						keyFieldsAreOnTheList = false;
						break;
					}
				}
				if (keyFieldsAreOnTheList) {
					if (element.getAttribute("TableAlias").equals("")) {
						tableID = element.getAttribute("ToTable");
					} else {
						tableID = element.getAttribute("TableAlias");
					}
					workTokenizer = new StringTokenizer(element.getAttribute("Fields"), ";" );
					while (workTokenizer.hasMoreTokens()) {
						wrkStr = workTokenizer.nextToken();
				    	dataSourceList.add(tableID + "." + wrkStr);
					}
				} else {
					JOptionPane.showMessageDialog(null, res.getString("ErrorMessage38"));
					result = false;
					break;
				}
			}
		}
		//
		return result;
	}

