	void repaintErrorStatusOfParentNodes(MainTreeNode tableListNode) {
		MainTreeNode workNode;
		boolean noError = true;
		//
		for (int i = 0; i < tableListNode.getChildCount(); i++) {
			workNode = (MainTreeNode)tableListNode.getChildAt(i);
			if (!workNode.getErrorStatus().equals("")) {
				noError = false;
				break;
			}
		}
		MainTreeNode subsystemNode = (MainTreeNode)tableListNode.getParent();
		if (noError) {
			tableListNode.setErrorStatus("");
			subsystemNode.setErrorStatus("");
		} else {
			tableListNode.setErrorStatus("ERR");
			subsystemNode.setErrorStatus("ERR");
		}
		//
		noError = true;
		MainTreeNode subsystemListNode = (MainTreeNode)subsystemNode.getParent();
		for (int i = 0; i < subsystemListNode.getChildCount(); i++) {
			workNode = (MainTreeNode)subsystemListNode.getChildAt(i);
			if (!workNode.getErrorStatus().equals("")) {
				noError = false;
				break;
			}
		}
		MainTreeNode systemNode = (MainTreeNode)subsystemListNode.getParent();
		if (noError) {
			subsystemListNode.setErrorStatus("");
			systemNode.setErrorStatus("");
		} else {
			subsystemListNode.setErrorStatus("ERR");
			systemNode.setErrorStatus("ERR");
		}
		//
		jTreeMain.repaint();
	}

	
